import { Component } from "react";
import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Divider from '@material-ui/core/Divider';
import FlashOnRoundedIcon from '@material-ui/icons/FlashOnRounded';


const StyledGrid = withStyles((theme) => ({
    root: {
        width: 'fit-content',
        border: `1px solid ${theme.palette.divider}`,
        margin: "auto",
        borderRadius: theme.shape.borderRadius,
        backgroundColor: theme.palette.background.paper,
        color: theme.palette.text.secondary,
        '& svg': {
            margin: theme.spacing(1.5),
          },
          '& hr': {
            margin: theme.spacing(0, 0.5),
          },
    }
}))(Grid);




class LightStatus extends Component {
    render(){
        return(            
            <div>
                <StyledGrid container alignItems="center">
                    
                    <text style={{padding: "10px", color: "black", textDecoration: "none"}}>{this.props.name}</text>
                    <Divider style={{height: "auto"}}  orientation="vertical" flexItem />
                    <FlashOnRoundedIcon style={{color: (this.props.data.lightInfo[0]['power'] === "on") ? "green" : "red", fontSize: "30px"}}></FlashOnRoundedIcon>
                    <Divider style={{height: "auto"}}  orientation="vertical" flexItem />
                    <i style={{fontSize: "25px", marginBottom: "15px"}} class="lightbulb icon"></i>
                </StyledGrid>
                
            </div>
        )
    }
}
export default LightStatus;
