import { Component } from "react";
import Button from 'react-bootstrap/Button';
import Slider from '@material-ui/core/Slider';

var token = "Bearer c21a99f98f3d64bce53933d23097cbd4fb880ecf476037f82be4c85eb84ce344";
class DisplayColor extends Component{
    state = {
        lightColor: "",
    }

    toggleColor(){
        const urlPower = "https://api.lifx.com/v1/lights/d073d5544584/state";
        const data = {
            "color": this.state.lightColor
        }
        fetch(urlPower, {
            method: 'PUT',
            headers: {
                'Authorization': token,
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
          })
            .then(response => response.json())
            .then((lightState) => {
                console.log(lightState['results'])
              })
            .catch((error) => {
              console.error(error);
            });
    }

    valuetext(value) {
        this.setState({
            lightColor: value
        })
      }

    render(){

        return (
            <div>
                <Button variant="primary" onClick={this.toggleColor.bind(this)}>Toggle Color</Button>
                <Slider
                    defaultValue={0}
                    getAriaValueText={this.valuetext.bind(this)}
                    aria-labelledby="discrete-slider-small-steps"
                    step={500}
                    marks
                    min={1500}
                    max={4500}
                    valueLabelDisplay="auto"
                />
            </div>
        )
    }
}

export default DisplayColor;
