import { Component } from "react";
import Button from 'react-bootstrap/Button';

var token = "Bearer c21a99f98f3d64bce53933d23097cbd4fb880ecf476037f82be4c85eb84ce344";

class TogglePower extends Component{
    state = {
        lightStatus: "off",
    }

    togglePower(){
        if (this.state.lightStatus == "off"){
            this.setState({
                lightStatus: "on"
            }) 
        } else{
            this.setState({
                lightStatus: "off"
            })
        }
        const urlPower = "https://api.lifx.com/v1/lights/all/toggle"
        fetch(urlPower, {
            method: 'POST',
            headers: {
                'Authorization': token
            },
          })
            .then(response => response.json())
            .then((powerStatus) => {
                console.log(powerStatus['results'])

                //console.log(powerStatus[0]['id']);
              })
            .catch((error) => {
              console.error(error);
            });
    }

    render(){
        return (
            <div className="container">
                <div className="row"></div>
                <Button variant="primary" onClick={this.togglePower.bind(this)}>Toggle Power</Button>
                <p>Light switched {this.state.lightStatus}</p>
            </div>
        )
    }
}

export default TogglePower;
