import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
import 'semantic-ui-css/semantic.min.css'
import ListLights from './ListLights/ListLights';
import React, { useState } from 'react';

const logoLink = "https://cdn.shopify.com/s/files/1/0219/0638/files/lifx_logo_9ae0aa6d-07df-41ba-b96d-0f400323e1b1_140x.png?v=1575366323"

function App() {
  const [apiKey, setApiKey] = useState();
  const [accessGranted, setAccessGranted] = useState();
  var displayLights = "";

  function handleChange(event){
    setApiKey(event.target.value)
  }

  function handleSubmit(event){
    event.preventDefault();
    setAccessGranted("yes")
  }

  if (accessGranted === "yes"){
    button = <ListLights></ListLights>
  } else{
    button = "Try Again"
  }
  return (
    <div className="App">
      <h1><img src={logoLink} alt="LifX"></img></h1>
      <form onSubmit={handleSubmit}>
        <label style={{padding: "10px"}}>API Key</label>
        <input type="text" value={apiKey} onChange={handleChange}></input>
        <input type="submit"></input>
        <p>{apiKey}</p>
        <p>{accessGranted}</p>
      </form>

      {button}

    </div>
  );
};

export default App;
