import {Component} from 'react';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';
import Form from 'react-bootstrap/Form';
import ListLights from '../ListLights/ListLights';

const url = "https://api.lifx.com/v1/lights/all";

class Authorise extends Component{
    state = {
        apiKey: "",
        authorised: false,
        display: "",
    }

    onInputChange(event){
        this.setState({
            apiKey: event.target.value
        })
    }

    onKeySubmit(){
        // Attempt to call API
        fetch(url, {
            method: 'GET',
            headers: {
                'Authorization': `Bearer ${this.state.apiKey}`
            },
          })
            .then(function(response){
                console.log(response.status);
                if (!response.ok){
                    alert("Invalid key. Try again.")
                    throw new Error("HTTP Status " + response.status);
                }
                return response.json();
            })
            .then((jsonResponse) => {
                console.log(jsonResponse)
                this.setState({
                    authorised: true,
                })
            })
            .catch((error) => {
                console.log(error)
            })
    }

    render(){
        const isAuthorised = this.state.authorised;
        let displayComponent;

        if (isAuthorised){
            displayComponent = <ListLights></ListLights>
        }
        return (
            {displayComponent}
