import { Component } from "react";
import PowerSettingsNewRoundedIcon from '@material-ui/icons/PowerSettingsNewRounded';
import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import SettingsBrightnessIcon from '@material-ui/icons/SettingsBrightness';
import Divider from '@material-ui/core/Divider';
import SettingsBrightness from "@material-ui/icons/SettingsBrightness";


const StyledGrid = withStyles((theme) => ({
    root: {
        width: 'fit-content',
        border: `1px solid ${theme.palette.divider}`,
        margin: "auto",
        borderRadius: theme.shape.borderRadius,
        backgroundColor: theme.palette.background.paper,
        color: theme.palette.text.secondary,
        '& svg': {
            margin: theme.spacing(1.5),
          },
          '& hr': {
            margin: theme.spacing(0, 0.5),
          },
    }
}))(Grid);




class LightStatus extends Component {
    render(){
        return(            
            <div>
                <StyledGrid container alignItems="center">
                    <text>{this.props.name}</text>
                    <Divider style={{height: "auto"}}  orientation="vertical" flexItem />
                    <PowerSettingsNewRoundedIcon style={{color: (this.props.data === "on") ? "green" : "red", fontSize: "25px"}}></PowerSettingsNewRoundedIcon>
                    <Divider style={{height: "auto"}}  orientation="vertical" flexItem />
                    <SettingsBrightnessIcon style={{fontSize: "25px"}}></SettingsBrightnessIcon>
                </StyledGrid>
                
            </div>
        )
    }
}
export default LightStatus;

