import { Component } from "react";
import PowerSettingsNewRoundedIcon from '@material-ui/icons/PowerSettingsNewRounded';



class LightStatus extends Component {

    componentDidUpdate(prevProps) {
        // Typical usage (don't forget to compare props):
        if (this.props.data.lightInfo[0]['power'] !== prevProps.data) {
            console.log("fetch")
            this.fetchData(this.props.data);
        }
      }

    render(){
        return(
            
            <div>
                <p>{this.props.data.lightInfo[0]['power']}</p>
                <input></input>
                <PowerSettingsNewRoundedIcon style={{color: (this.props.data.lightInfo[0]['power'] == "on") ? "green" : "red", fontSize: "35px"}}></PowerSettingsNewRoundedIcon>
            </div>
        )
    }
}

export default LightStatus;
