import { Component } from "react";
import PowerSettingsNewRoundedIcon from '@material-ui/icons/PowerSettingsNewRounded';



class LightStatus extends Component {
    state = {
        lightInfo: this.props.data
    }

    UNSAFE_componentWillReceiveProps(nextProps){
        if (nextProps.data !== this.props.data){
            console.log("update")
            this.setState({
                lightInfo: nextProps.data
            })
        }
    }

    render(){
        return(
            
            <div>
                <p>{this.props.data[0]['power']}</p>
                <input></input>
                <PowerSettingsNewRoundedIcon style={{color: (this.props.data[0]['power'] == "on") ? "green" : "red", fontSize: "35px"}}></PowerSettingsNewRoundedIcon>
            </div>
        )
    }
}
export default LightStatus;
