import { Component } from "react";
import PowerSettingsNewRoundedIcon from '@material-ui/icons/PowerSettingsNewRounded';
import ThreeDRotation from '@material-ui/icons/ThreeDRotation';

class LightStatus extends Component {


    render(){
        const arrIndex = this.props.data.lightLabels.indexOf(this.props.name)
        const lightID = this.props.data.lightInfo[arrIndex]['id'];
        var powerStatus = this.props.data.lightInfo[arrIndex]['power']

        return(
            
            <div>
                <PowerSettingsNewRoundedIcon style={{color: (this.props.data.lightInfo[arrIndex]['power'] == "on") ? "green" : "red", fontSize: "35px"}}></PowerSettingsNewRoundedIcon>
            </div>
        )
    }
}

export default LightStatus;
