import { Component } from "react";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Accordion from 'react-bootstrap/Accordion';
import TogglePower from "../TogglePower/TogglePower";
import DisplayColor from "../DisplayColor/DisplayColor";
import LightStatus from "../LightStatus/LightStatus";


var token = "Bearer c21a99f98f3d64bce53933d23097cbd4fb880ecf476037f82be4c85eb84ce344";

class ListLights extends Component{
    state = {
        lightInfo: "",
        lightLabels: [],
    }

    fetchData(){}

    componentDidMount(){
       
        const url = "https://api.lifx.com/v1/lights/all";
        fetch(url, {
            method: 'GET',
            headers: {
                'Authorization': token
            },
          })
            .then(response => response.json())
            .then((lightProps) => {
 
                this.setState({lightInfo: lightProps})
                
                for (let i = 0; i < this.state.lightInfo.length; i++){
                    var joined = this.state.lightLabels.concat(this.state.lightInfo[i]['label']);
                    this.setState({ lightLabels: joined })
                }             
              })
            .catch((error) => {
              console.error(error);
            });
    }


    render(){
        return (
            
            <Accordion>
                {this.state.lightLabels.map(listitem => (
                    <Card key={listitem}>
                        <Card.Header>
                            <Accordion.Toggle as={Button} variant="link" eventKey={this.state.lightLabels.indexOf(listitem).toString()}>
                                {listitem}
                            </Accordion.Toggle>
                        </Card.Header>
                        <Accordion.Collapse eventKey={this.state.lightLabels.indexOf(listitem).toString()}>
                            <Card.Body>
                                
                                {/* Displays light status component */}
                                <LightStatus data={this.state}dataFromParent = {[this.state.lightInfo[this.state.lightLabels.indexOf(listitem)]['id'],
                                this.state.lightInfo[this.state.lightLabels.indexOf(listitem)]['power'] ]}></LightStatus>
                                <br></br>
                                {/* Displays the power status and passes the id and power status of this light to the Toggle Power component */}
                                <TogglePower dataFromParent = {[this.state.lightInfo[this.state.lightLabels.indexOf(listitem)]['id'],
                                this.state.lightInfo[this.state.lightLabels.indexOf(listitem)]['power'] ]}></TogglePower>
                                <br></br>
                                {/* Displays color switcher component */}
                                <DisplayColor></DisplayColor>
                            </Card.Body>
                        </Accordion.Collapse>
                    </Card>
                ))}
            </Accordion>
        )
    }
}

export default ListLights;
