import { Component } from "react";
import PowerSettingsNewRoundedIcon from '@material-ui/icons/PowerSettingsNewRounded';



class LightStatus extends Component {
    state = {
        lightInfo: this.props.data.lightInfo
    }

    UNSAFE_componentWillReceiveProps(nextProps) {
        this.setState({ lightInfo: nextProps.ligtInfo });  
      }

    render(){

        return(
            
            <div>
                <p>{this.props.data.lightInfo[0]['power']}</p>
                <PowerSettingsNewRoundedIcon style={{color: (this.state.power == "on") ? "green" : "red", fontSize: "35px"}}></PowerSettingsNewRoundedIcon>
            </div>
        )
    }
}

export default LightStatus;
