import { Component } from "react";
import Button from 'react-bootstrap/Button';
import { makeStyles } from '@material-ui/core/styles';
import Slider from '@material-ui/core/Slider';

var token = "Bearer c21a99f98f3d64bce53933d23097cbd4fb880ecf476037f82be4c85eb84ce344";

const useStyles = makeStyles((theme) => ({
    root: {
      width: 300,
    },
    margin: {
      height: theme.spacing(3),
    },
  }));

class DisplayColor extends Component{
    state = {
        lightColor: "kelvin:4000",
    }

    toggleColor(){
        const urlPower = "https://api.lifx.com/v1/lights/d073d5544584/state";
        const data = {
            "color": this.state.lightColor
        }
        fetch(urlPower, {   
            method: 'PUT',
            headers: {
                'Authorization': token,
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
          })
            .then(response => response.json())
            .then((lightState) => {
                console.log(lightState['results'])
              })
            .catch((error) => {
              console.error(error);
            });
    }

    valuetext(value) {
        return `kelvin${value}`;
    }

    sliderChange(event, value){
        console.log(value)
        this.setState({
            lightColor: `kelvin:${value}`
        })
    }


    render(){

        return (
            <div>
                <Button variant="primary" onClick={this.toggleColor.bind(this)}>Switch Color</Button>
                <Slider
                    defaultValue={3500}
                    getAriaValueText={this.valuetext.bind(this)}
                    onChangeCommitted={this.sliderChange.bind(this)}
                    aria-labelledby="discrete-slider-small-steps"
                    step={500}
                    marks
                    min={1500}
                    max={4500}
                    valueLabelDisplay="auto"
                />
            </div>
        )
    }
}

export default DisplayColor;
