import { Component } from "react";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Accordion from 'react-bootstrap/Accordion';
import TogglePower from "../TogglePower/TogglePower";


var token = "Bearer c21a99f98f3d64bce53933d23097cbd4fb880ecf476037f82be4c85eb84ce344";

class ListLights extends Component{
    state = {
        lightInfo: "",
        lightLabels: ["Kitchen", "Living Room"],
    }

    fetchData(){}

    componentDidMount(){
       
        const url = "https://api.lifx.com/v1/lights/all";
        fetch(url, {
            method: 'GET',
            headers: {
                'Authorization': token
            },
          })
            .then(response => response.json())
            .then((lightProps) => {
                console.log(lightProps.length)
                console.log(lightProps[0]['label']);
                this.setState({lightInfo: lightProps})
                
                for (let i = 0; i < this.state.lightInfo.length; i++){
                    var joined = this.state.lightLabels.concat(this.state.lightInfo[i]['label']);
                    console.log(this.state.lightInfo[i]['label'], "test")
                    this.setState({ lightLabels: joined })
                }

                console.log(this.state.lightLabels)                
              })
            .catch((error) => {
              console.error(error);
            });
    }


    render(){
        return (
            
            <Accordion>
                {this.state.lightLabels.map(listitem => (
                    <Card>
                        <Card.Header>
                            <Accordion.Toggle as={Button} variant="link" eventKey={this.state.lightLabels.indexOf(listitem).toString()}>
                                {listitem}
                            </Accordion.Toggle>
                        </Card.Header>
                        <Accordion.Collapse eventKey={this.state.lightLabels.indexOf(listitem).toString()}>
                            
                            <Card.Body>
                                <TogglePower></TogglePower>
                            </Card.Body>
                        </Accordion.Collapse>
                    </Card>
                ))}
            </Accordion>
