import React, { Component } from "react";
import Button from 'react-bootstrap/Button';

var token = "Bearer c21a99f98f3d64bce53933d23097cbd4fb880ecf476037f82be4c85eb84ce344";
const url = "https://api.lifx.com/v1/lights/all";

class CallAPI extends Component{
    state = {
        label: '',
        id: "",
    };

    componentDidMount(){
        fetch(url, {
            method: 'GET',
            headers: {
                'Authorization': token
            },
          })
            .then(response => response.json())
            .then((lightProps) => {
                console.log(lightProps.length)
                console.log(lightProps[0]['label']);
                this.setState({label: lightProps[0]['label']})
                this.setState({id: lightProps[0]['id']})
              })
            .catch((error) => {
              console.error(error);
            });
    }

    listLightFunction(){
        console.log("This is a test");
        for (var i = 0; i< this.state.lights.length; i++){
            console.log(this.state.lights[i]['label'])
        }
    }

    async powerOn(){
        
        const urlPower = "https://api.lifx.com/v1/lights/all/state"
        const response = await fetch(urlPower, {
            method: 'PUT',
            headers: {
                'Authorization': token
            },
            body: {
                'Power': "on"
            }
        });
        console.log("success")
        return await response.json();
    }

    render(){
        return (
            <div>
                <Button variant="primary" onClick={this.powerOn}>Power On</Button>
                <p>Label: {this.state.label}</p>
                <p>ID: {this.state.id}</p>
                <p></p>
            </div>
        )
    }

}

export default CallAPI;
