import {Component} from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import ListLights from '../ListLights/ListLights';

const url = "https://api.lifx.com/v1/lights/all";

class Authorise extends Component{
    state = {
        apiKey: "",
        authorised: "Please enter a valid LifX API key to continue",
        display: "",
    }

    onInputChange(event){
        this.setState({
            apiKey: event.target.value
        })
    }

    onKeySubmit(){
        // Attempt to call API
        fetch(url, {
            method: 'GET',
            headers: {
                'Authorization': `Bearer ${this.state.apiKey}`
            },
          })
            .then(response => response.ok)
            .then((success) => {  
                console.log(response)
                console.log(success)
                if (success['error'] === "Credentials required"){
                    this.setState({
                        authorised: "Please enter a valid LifX API key to continue"
                    })
                } else {
                    this.setState({
                        authorised: "Success!",
                        display: "none"
                    })
                }
              })
            .catch((error) => {
              console.error(error);
              this.setState({
                  authorised: "Please enter a valid LifX API key to continue"
              })

            });
    }

    render(){

        return (
            <div style={{width:"300px", margin: "auto"}}>
                <Form>
                    <Form.Group className="mb-2" controlId="formBasicEmail">
                        <Form.Label>API Key</Form.Label>
                        <Form.Control type="text" placeholder="Enter API key" onChange={this.onInputChange.bind(this)} />
                        <Form.Text className="text-muted">
                            {this.state.authorised}
                        </Form.Text>
                        <Button style={{marginTop: "10px"}} variant="primary" onClick={this.onKeySubmit.bind(this)}>
                            Submit
                        </Button>
                    </Form.Group>
                </Form>
            </div>
        )
    }

}

export default Authorise;
