import { Component } from "react";
import Button from 'react-bootstrap/Button';
import { withStyles } from '@material-ui/core/styles';
import Slider from '@material-ui/core/Slider';

var token = "Bearer c21a99f98f3d64bce53933d23097cbd4fb880ecf476037f82be4c85eb84ce344";

const StyledSlider = withStyles({
    root: {
      color: "linear-gradient(90deg, rgba(255,109,0,1) 0%, rgba(255,138,18,1) 16%, rgba(255,161,72,1) 32%, rgba(255,180,107,1) 48%, rgba(255,196,137,1) 64%, rgba(255,209,163,1) 80%, rgba(255,219,186,1) 96%);",
    },
    rail: {
        height: "10px"
    },

  })(Slider);


class DisplayColor extends Component{
    state = {
        lightColor: "kelvin:4000",
    }


    toggleColor(){
        const urlPower = "https://api.lifx.com/v1/lights/d073d5544584/state";
        const data = {
            "color": this.state.lightColor
        }
        fetch(urlPower, {   
            method: 'PUT',
            headers: {
                'Authorization': token,
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
          })
            .then(response => response.json())
            .then((lightState) => {
                console.log(lightState['results'])
              })
            .catch((error) => {
              console.error(error);
            });
    }

    valuetext(value) {
        return `kelvin${value}`;
    }

    sliderChange(event, value){
        console.log(value)
        this.setState({
            lightColor: `kelvin:${value}`
        })
    }


    render(){
        const useStyles = {
            root: {
                color: "black"
            }
        }
        
        return (
            <div>
                <Button variant="primary" onClick={this.toggleColor.bind(this)}>Switch Colour</Button>
                <StyledSlider
                    defaultValue={3500}
                    getAriaValueText={this.valuetext.bind(this)}
                    onChangeCommitted={this.sliderChange.bind(this)}
                    aria-labelledby="discrete-slider-small-steps"
                    step={500}
                    marks
                    min={1500}
                    max={4500}
                    valueLabelDisplay="auto"
                />
            </div>
        )
    }
}

export default DisplayColor;
