import { Component } from "react";
import Button from 'react-bootstrap/Button';

var token = "Bearer c21a99f98f3d64bce53933d23097cbd4fb880ecf476037f82be4c85eb84ce344";

class ListLights extends Component{
    state = {
        lightInfo: "",
        listitems: ["List Item 1", "List Item 2", "List Item 3"],
    }

    fetchData(){
        const url = "https://api.lifx.com/v1/lights/all";
        fetch(url, {
            method: 'GET',
            headers: {
                'Authorization': token
            },
          })
            .then(response => response.json())
            .then((lightProps) => {
                console.log(lightProps.length)
                console.log(lightProps[0]['label']);
                this.setState({lightInfo: lightProps})
                
                for (let i = 0; i < this.state.lightInfo.length; i++){
                    console.log(this.state.lightInfo[i]['label'], "test")
                }
                
              })
            .catch((error) => {
              console.error(error);
            });
    }

    render(){
        return (
            <div className="container">
                <div className="row justify-content-md-center">
                    <div className="col col-md-2">
                
                            <ul className="list-group">
                                {this.state.listitems.map(listitem => (
                                <li className="list-group-item list-group-item-primary">
                                    </li>
                                ))}
                            </ul>
                    
                        <Button variant="primary" onClick={this.fetchData.bind(this)}>List Lights</Button>
                    </div>
                </div>
            </div>
        )
    }
}

export default ListLights;
