import { Component } from "react";
import ListLights from "../ListLights/ListLights";
import Button from 'react-bootstrap/Button';

var token = "Bearer c21a99f98f3d64bce53933d23097cbd4fb880ecf476037f82be4c85eb84ce344";

class TogglePower extends Component{
    state = {
        lightInfo: this.props.data.lightInfo,
    }

    

    togglePower(){
        const lightID = this.props.data.lightInfo[this.props.data.lightLabels.indexOf(this.props.name)]['id'];
        const urlPower = `https://api.lifx.com/v1/lights/${lightID}/toggle`;
        fetch(urlPower, {
            method: 'POST',
            headers: {
                'Authorization': token
            },
          })
            .then(response => response.json())
            .then((powerStatus) => {
              })
            .catch((error) => {
              console.error(error);
            });
    }

    lightSwitch(){
        const lightID = this.props.data.lightInfo[this.props.data.lightLabels.indexOf(this.props.name)]['id'];
        var powerStatus = this.props.data.lightInfo[this.props.data.lightLabels.indexOf(this.props.name)]['power']

        const changePower = (powerStatus == "on") ? "off" : "on"

        const urlState = `https://api.lifx.com/v1/lights/${lightID}/state`;
        const data = {
            "power": changePower
        }
        fetch(urlState, {   
            method: 'PUT',
            headers: {
                'Authorization': token,
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
          })
            .then(response => response.json())
            .then((lightState) => {
                console.log(lightState['results'])
              })
            .catch((error) => {
              console.error(error);
            });
    }

    tryThis(){
        this.props.upLift("random");
    }

    render(){
        return (
            <div className="container">
                <div className="row justify-content-md-center">
                    <div className="col col-md-2">
                        <p>{this.props.data.power}</p>
                        <Button variant="primary" onClick={this.updateParentState.bind(this)}>Test</Button>
                        <Button variant="primary" onClick={this.lightSwitch.bind(this)}>Toggle Power</Button>
                    </div>
                </div>
            </div>
        )
    }
}

export default TogglePower;
