import React, { Component } from "react";

var token = "Bearer c21a99f98f3d64bce53933d23097cbd4fb880ecf476037f82be4c85eb84ce344";
const url = "https://api.lifx.com/v1/lights/all";

class CallAPI extends React.Component{
    state = {
        lights: '',
    };

    componentDidMount(){
        fetch(url, {
            method: 'GET',
            headers: {
                'Authorization': token
            },
          })
            .then(response => response.json())
            .then((lightProps) => this.setState({lights: lightProps}))
            .catch((error) => {
              console.error(error);
            });
    }

    render(){
        return (
            <div><p>{this.lights}</p></div>
        )
    }

}

console.log(this.state.lights.lightProps);
export default CallAPI;
